import '../styles/movies.css'
import { useState } from 'react'
const Movies = () => {
    let favourites=[
    {   title:"The Boys",
        Genre:"Action",
        Rating:4.5,
        id:1
    },
    {   title:"Dark",
    Genre:"Sci-fi",
    Rating:4,
    id:2
},
{   title:"The Offce",
Genre:"Sitcom",
Rating:4.5,
id:3
},
{   title:"Friends",
Genre:"Comedy",
Rating:5,
id:4
},
{   title:"Berserker",
Genre:"Action",
Rating:5,
id:5
}
    ]
    let [favouritesData,setFavData]=useState(favourites)
    let handleRemove=(id,title)=>{
    setFavData(favouritesData.filter(x=> x.id!=id)) //setFavData is updater and updates the value n displayes it
    alert(`${title} has been removed`) 
    }
    return ( 
        <div className="movies">
            <h1>TOP WATCHED</h1>
            <div className="favs">
                {favouritesData.map((data)=>(
                    <div className="favs_list">
                        <h2>{data.title}</h2>
                        <p>{data.Genre}/{data.Rating}‚≠ê</p>
                        <button onClick={()=>handleRemove(data.id,data.title)}>Remove</button> 
                        {/* data.id will call id mentioned in the above  function parameter and will check if its calling same value and display with alert */}
                    </div>
                ))}
            </div>
        </div>
     );
}
 
export default Movies;
