import '../styles/movies.css'
import { useState } from 'react'
import MoviesLogic from './moviesLogic'
const Movies = () => {
    let favourites=[
    {   title:"The Boys",
        Genre:"Action",
        Rating:4.5,
        id:1
    },
    {   title:"Dark",
    Genre:"Sci-fi",
    Rating:4,
    id:2
},
{   title:"The Offce",
Genre:"Sitcom",
Rating:4.5,
id:3
},
{   title:"Friends",
Genre:"Comedy",
Rating:5,
id:4
},
{   title:"Berserker",
Genre:"Action",
Rating:5,
id:5
}
    ]
    let [favouritesData,setFavData]=useState(favourites)
    let handleRemove=(id,title)=>{
    setFavData(favouritesData.filter(x=> x.id!=id)) //setFavData is updater and updates the value n displayes it
    alert(`${title} has been removed`) 
    }
    return ( 
        <div className="movies">
           <MoviesLogic title="Favourites of all time" data={favouritesData} removeFunction={handleRemove}/>
           <MoviesLogic title="Top rated" data={favouritesData.filter(x=>x.Rating>4)} removeFunction={handleRemove}/>
           <MoviesLogic title="Top rated" data={favouritesData.filter(x=>x.Genre=Action)} removeFunction={handleRemove}/>
        </div>
     );
}

export default Movies;
